mapper.update:
update dr_product_invest_repayinfo
	set factPrincipal = #{factPrincipal},
		factInterest = #{factInterest},
		status = #{status},
		factTime = #{factTime}
where id = #{id}

update.foreach:
update  dr_product_invest_repayinfo

	 fileStatus = #{item.fileStatus},
 	 fuiouMessageNo = #{item.fuiouMessageNo}

where id = #{item.id:INTEGER}

mapper.update:
update dr_product_invest_repayinfo
remit_status = #{remitStatus}, remit_fail_reson = #{remitFailReson}, remitMchntTxnSsn = #{RemitMchntTxnSsn}, transfer_status = #{transfer_status}, transfer_fail_reson = #{transfer_fail_reson}, transferMchntTxnSsn = #{transferMchntTxnSsn},
where id = #{id}

update.foreach:
update dr_product_invest_repayinfo
remit_status = #{var.remitStatus}, remit_fail_reson = #{var.remitFailReson}, remitMchntTxnSsn = #{var.RemitMchntTxnSsn}, transfer_status = #{var.transfer_status}, transfer_fail_reson = #{var.transfer_fail_reson}, transferMchntTxnSsn = #{var.transferMchntTxnSsn},
where id = #{var.id}
