mapper.select:
SELECT CONCAT(left(dm.mobilePhone,3),'****',right(dm.mobilePhone,4)) mobilePhone,dpi.amount,dpi.investTime, if(LOCATE(',',apii.luckCodes)=0,apii.luckCodes,CONCAT(LEFT(apii.luckCodes,LOCATE(',',apii.luckCodes)),'...')) luckCodes from js_activity_product_invest_info apii LEFT JOIN dr_product_invest dpi ON apii.piid = dpi.id LEFT JOIN dr_member dm on dm.uid = dpi.uid LEFT JOIN js_activity_product jap ON jap.pid = dpi.pid
and dpi.pid = #{pid}
and jap.atid = #{atid}

SELECT ai.id,ai.piid,ai.luckCodes luckCodes,ai.prizeStatus,ai.isUplod,IFNULL(CONCAT(LEFT(ai.prizeMobile,3),"****",RIGHT(ai.prizeMobile,4)),NULL) prizeMobile,ai.prizeImgUrl, ai.prizeContent,jap.activityPeriods,jap.prizeCode,jap.pid,ai.prizeHeadPhoto,ai.prizeVideoLink,jap.appTitle,jap.pcDetailImg,jap.prizeUrl ,if(LENGTH(ai.prizeHeadPhoto) and LENGTH(ai.prizeContent),1,0) as seqs from js_activity_product_invest_info ai LEFT JOIN dr_product_invest dpin ON ai.piid = dpin.id LEFT JOIN js_activity_product jap ON jap.pid = dpin.pid WHERE ai.prizeStatus = 2
and jap.pid = #{pid}
and jap.pid != #{noPid}
GROUP BY jap.activityPeriods ORDER BY seqs desc,ai.id desc
limit #{offset},#{limit}

select count(1) from (
		SELECT  1 from js_activity_product_invest_info ai 
	LEFT JOIN dr_product_invest dpin ON ai.piid = dpin.id	
	LEFT JOIN js_activity_product jap ON jap.pid = dpin.pid
WHERE ai.prizeStatus = 2 

  	and jap.pid = #{pid}


	and jap.pid != #{noPid}

)a

SELECT dpi.id investId,dp.id pid,CONCAT(jat.`name`,'(第',jap.activityPeriods,'期)') activityName,dp.fullName productName,dpi.amount,japii.prizeStatus,japii.luckCodes,jap.prizeCode,
(SELECT GROUP_CONCAT(japr.`name`) from js_activity_prize japr where japr.atid = jat.id ) activityPrizes
	from js_activity_product_invest_info japii  
	LEFT JOIN dr_product_invest dpi ON japii.piid = dpi.id 
	LEFT JOIN dr_product_info dp ON dp.id = dpi.pid AND dp.atid is not NULL AND dp.`status` in (5,6,8,9) 
	LEFT JOIN js_activity_product jap ON dp.id = jap.pid
	LEFT JOIN js_activity_template jat ON jat.id = jap.atid

	
		and dpi.uid = #{uid}
	
	
		and jap.atid = #{atid}
	
	
		and jap.status in 
		 
		 	#{item}
		 	
	

ORDER BY japii.id DESC
limit #{offset},#{limit}

SELECT ifnull(count(1),0)
	from js_activity_product_invest_info japii  
	LEFT JOIN dr_product_invest dpi ON japii.piid = dpi.id 
	LEFT JOIN dr_product_info dp ON dp.id = dpi.pid AND dp.atid is not NULL AND dp.`status` in (5,6,8,9) 
	LEFT JOIN js_activity_product jap ON dp.id = jap.pid
	LEFT JOIN js_activity_template jat ON jat.id = jap.atid

	
		and dpi.uid = #{uid}
	
	
		and jap.atid = #{atid}
	
	
		and jap.pid = #{pid}
	
	
		and jap.pid != #{noPid}
	
	
		and jap.status in 
		 
		 	#{item}

select GROUP_CONCAT(japii.luckCodes) luckCodes  
		FROM js_activity_product_invest_info japii 
		LEFT JOIN dr_product_invest dpi ON dpi.id = japii.piid				

	
		and dpi.pid = #{pid}
	
	
		and dpi.uid = #{uid}

select GROUP_CONCAT(japii.luckCodes) luckCodes   , if(sum(japii.prizeStatus) !=0 ,1,0) prizeStatus
		FROM js_activity_product_invest_info japii 
		LEFT JOIN dr_product_invest dpi ON dpi.id = japii.piid				

	
		and dpi.pid = #{pid}
	
	
		and dpi.uid = #{uid}
	
	
		and dpi.id = #{investId}

SELECT a.*,
CONCAT("投资了第",a.activityPeriods,"期",a.amount,"元获得",a.luckNum,"个幸运号码。",
	
	IF(a.luckNum<5,"好运的第一步是尝试！",
			IF(a.luckNum<10,"我想中奖，别问我中奖是谁！",
					IF(a.luckNum<51,"召唤洪荒之力助我中奖！",
						IF(a.luckNum<100,"努力实现获得iPhone 7的小目标！",
								IF(a.luckNum<200,"欧气十足，抽奖码我们走！",
										IF(a.luckNum<300,"这片抽奖码被承包了！","天了噜，土豪包标啦！")
									)
							)
					)
				)
		)
	  
) as text  FROM  (
SELECT CONCAT(LEFT(dmi.realname,1),IF(dmi.sex=1,'先生','女士')) as realname, DATE_FORMAT(dpi.investTime,'%Y-%m-%d %H:%i') as investTime,jap.activityPeriods,
SUM(length(japi.luckCodes)-length(replace(japi.luckCodes,',',''))+1) as luckNum,TRUNCATE(sum(dpi.amount),0) amount
from js_activity_product_invest_info japi
LEFT JOIN dr_product_invest dpi ON japi.piid = dpi.id
LEFT JOIN js_activity_product jap ON jap.pid = dpi.pid
LEFT JOIN dr_member_base_info dmi ON dmi.uid = dpi.uid
where jap.activityPeriods  GROUP BY jap.activityPeriods,dpi.uid  ORDER BY dpi.investTime DESC limit 20
)a
