mapper.select:
select dcl.id,dcc.id customerId,dcc.user_id,dcc.fuiou_acnt,dcl.name,dcl.no,dcl.status,dcl.loanUse,ifnull(dcl.loanAmount,0) as loanAmount,dcl.receiveInterest,
IFNULL(IFNULL(dcl.receiveInterest,0)+IFNULL(dcl.loanAmount,0),0) as pendRepayAmount,dcl.currency,dcl.loanDeadline,dcl.serviceCharge,
ifnull(dcl.rate,0) as rate,dcl.repayType,dcl.repayDeadline,dcl.startDate,dcl.endDate,
dcl.dateType,dcl.loanName,dcl.bankName,dcl.bankAddress,CONCAT(left(dcl.bankNo,4),'****',right(dcl.bankNo,4)) as bankNo,dcl.advisoryRate,dcl.serviceRate,dcl.defaultersRate,dcl.showStatus,
dcl.overdueRate,dcl.overduePenaltyRate,dcl.riskRate,dcl.addDate,dcl.addUser,dcl.updDate,dcl.updUser,dcc.companyName,dcc.companyNameProtocolShow,dcl.isAuditEdit,
dcc.mchnt_txn_ssn as mchnt_txn_ssn
from dr_claims_loan dcl
LEFT JOIN dr_claims_customer dcc ON dcl.id = dcc.lid

	
		dcl.name like CONCAT('%','${name}','%' )
	
	 
		and dcl.no like CONCAT('%','${no}','%' )
	
	 
		and dcc.companyName like CONCAT('%','${companyName}','%' )
	
	 
		and DATE_FORMAT(dcl.addDate,'%Y-%m-%d') >= #{startDate}
	
	
		and DATE_FORMAT(dcl.addDate,'%Y-%m-%d') <= #{endDate} 
	
	 
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') >= #{dueStartDate}
	
	
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') <= #{dueEndDate} 
	
	
		and (dcl.status in 
		  
		 #{item}  
						
		
			 or dcl.isAuditEdit = 1 
		
		)
	
	
	
		and dcl.loanAmount = #{loanAmount}
	
	
		and dcl.isAuditEdit = 1
								
	

 order by dcl.addDate desc

limit #{offset},#{limit}

select count(1) from dr_claims_loan dcl
LEFT JOIN dr_claims_customer dcc ON dcl.id = dcc.lid

	
		dcl.name like CONCAT('%','${name}','%' )
	
	 
		and dcl.no like CONCAT('%','${no}','%' )
	
	 
		and dcc.companyName like CONCAT('%','${companyName}','%' )
	
	 
		and DATE_FORMAT(dcl.addDate,'%Y-%m-%d') >= #{startDate}
	
	
		and DATE_FORMAT(dcl.addDate,'%Y-%m-%d') <= #{endDate} 
	
	
	 
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') >= #{dueStartDate}
	
	
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') <= #{dueEndDate} 
	
	
		and (dcl.status in 
		  
		 #{item}  
						
		
			 or dcl.isAuditEdit = 1 
		
		)
	
	
		and dcl.loanAmount = #{loanAmount}
	
	
		and dcl.isAuditEdit = 1

select IFNULL(sum(IFNULL(dcl.receiveInterest,0)+IFNULL(dcl.loanAmount,0)),0) as pendRepayAmount from dr_claims_loan dcl
LEFT JOIN dr_claims_customer dcc ON dcl.id = dcc.lid

	 
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') >= #{dueStartDate}
	
	
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') <= #{dueEndDate} 
	
	
		and	dcl.status in 
		  
		 #{item}

mapper.update:
update dr_claims_loan
name = #{name:VARCHAR}, no = #{no:VARCHAR}, status = #{status:INTEGER}, loanUse = #{loanUse:VARCHAR}, loanAmount = #{loanAmount:DECIMAL}, receiveInterest = #{receiveInterest:DECIMAL}, serviceCharge = #{serviceCharge:DECIMAL}, receiveInterest = #{receiveInterest:DECIMAL}, currency = #{currency:VARCHAR}, loanDeadline = #{loanDeadline:VARCHAR}, rate = #{rate:DECIMAL}, repayType = #{repayType:INTEGER}, repayDeadline = #{repayDeadline:INTEGER}, startDate = #{startDate:TIMESTAMP}, endDate = #{endDate:TIMESTAMP}, dateType = #{dateType:INTEGER}, loanName = #{loanName:VARCHAR}, bankName = #{bankName:VARCHAR}, bankAddress = #{bankAddress:VARCHAR}, bankNo = #{bankNo:VARCHAR}, advisoryRate = #{advisoryRate:DECIMAL}, serviceRate = #{serviceRate:DECIMAL}, defaultersRate = #{defaultersRate:DECIMAL}, overdueRate = #{overdueRate:DECIMAL}, overduePenaltyRate = #{overduePenaltyRate:DECIMAL}, riskRate = #{riskRate:DECIMAL}, updDate = sysdate(), updUser = #{updUser:INTEGER}, cardFlag = #{cardFlag:INTEGER}, isAuditEdit = #{isAuditEdit:INTEGER}
where id = #{id:INTEGER}

mapper.select:
SELECT loan.* from dr_claims_loan loan
left join dr_subject_info sub on loan.id = sub.lid

	sub.id = #{sid:INTEGER}

select * from dr_claims_loan 

	 
		no = #{no:VARCHAR}
	
	 
		and id = #{id:INTEGER}

select dcl.no,dcl.name,dcl.loanAmount,dcl.status,dcl.bankName,CONCAT(LEFT(dcl.bankNo,4),'****',RIGHT(dcl.bankNo,4)) AS bankNo, CASE WHEN dcl.status=6 THEN 0 ELSE dcl.receiveInterest+dcl.loanAmount END as pendRepayAmount,dcl.loanDeadline,dcl.rate ,dcl.addDate,dcl.endDate,dsi.code,dsi.surplusAmount,count(dpi.id) as productCounts from dr_claims_loan dcl LEFT JOIN dr_subject_info dsi on dsi.lid = dcl.id left JOIN dr_product_info dpi on dpi.sid = dsi.id
dcl.name like CONCAT('%','${name}','%' )
and dcl.no like CONCAT('%','${no}','%' )
and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') >= #{dueStartDate}
and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') <= #{dueEndDate}
and dcl.status in
#{item}
GROUP BY dcl.id order by dcl.addDate desc limit #{offset},#{limit}

select count(1) from dr_claims_loan dcl

	
		dcl.name like CONCAT('%','${name}','%' )
	
	 
		and dcl.no like CONCAT('%','${no}','%' )
	
	 
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') >= #{dueStartDate}
	
	
		and DATE_FORMAT(dcl.endDate,'%Y-%m-%d') <= #{dueEndDate} 
	
	
		and	dcl.status in 
		  
		 #{item}

select dcl.id,dcl.name,dcl.no,dcl.status,ifnull(dcl.loanAmount,0) as loanAmount,dcl.receiveInterest, dcl.serviceCharge, dcl.startDate,dcl.endDate,dcl.showStatus, dcl.addDate,dcc.companyName, dcc.companyNameProtocolShow from dr_claims_loan dcl LEFT JOIN dr_claims_customer dcc ON dcl.id = dcc.lid
dcl.name like CONCAT('%','${name}','%' )
and dcl.no like CONCAT('%','${no}','%' )
and dcc.companyName like CONCAT('%','${companyName}','%' )
and dcc.companyNameProtocolShow like CONCAT('%','${companyNameProtocolShow}','%' )
and DATE_FORMAT(dcl.addDate,'%Y-%m-%d') >= #{startDate}
and DATE_FORMAT(dcl.addDate,'%Y-%m-%d') <= #{endDate}
and dcl.status in
#{item}
and dcl.loanAmount = #{loanAmount}
order by dcl.addDate desc
