mapper.select:
select dpi.id as dpid,dpit.id as id,dpi.code as code,dm.mobilePhone as out_cust_no, cust.phone as in_cust_no,dpi.itemNo,ifNull(dpit.fileStatus,1) as fileStatus, dpit.amount as amount,dpit.mchnt_txn_ssn as mchntTxnSsn,dpit.failureCause, LEFT(dpit.mchnt_txn_ssn,8) AS mchTime, dplr.contractCode as contractCode,cust.companyName as name,dmbi.realname as realname
select dpi.id as dpid,dpit.id as id,dpi.code as code,dm.mobilePhone as out_cust_no, cust.phone as in_cust_no,dpi.itemNo,ifNull(dpit.fullFileStatus,1) as fullFileStatus, dpit.factAmount as amount,dpit.remitMchntTxnSsn as mchntTxnSsn,dpit.fullFailureCause as failureCause, LEFT(dpit.remitMchntTxnSsn,8) as mchTime, dplr.contractCode as contractCode,cust.companyName as name,dmbi.realname as realname
select dpi.id as dpid,dpitr.id as id,dpi.code as code, case when dpitr.mchntPay = 1 Then 'tf002' ELSE cust.phone END as out_cust_no , dm.mobilePhone as in_cust_no,ifNull(dpitr.fileStatus,1) as fileStatus, dplr.contractCode as contractCode,dpi.itemNo, dpitr.shouldTime as shouldTime, (dpitr.shouldPrincipal + dpitr.shouldInterest) as amount, dpitr.remitMchntTxnSsn as mchntTxnSsn,dpitr.failureCause, LEFT(dpitr.remitMchntTxnSsn,8) as mchTime, cust.companyName as name,case when dpitr.mchntPay = 1 Then '慧信财富管理有限公司' ELSE dmbi.realname END as realname
from dr_product_invest dpit left join dr_product_info dpi on dpit.pid=dpi.id left join dr_member dm on dm.uid=dpit.uid left join dr_subject_info sub ON dpi.sid = sub.id left join dr_claims_loan loan ON loan.id = sub.lid left join dr_claims_customer cust ON cust.lid = loan.id left join dr_product_loan_record dplr on dplr.pid=dpi.id left join dr_member_base_info dmbi on dmbi.uid=dm.uid
from dr_product_invest_repayinfo dpitr left join dr_product_info dpi on dpitr.pid=dpi.id left join dr_member dm on dm.uid=dpitr.uid left join dr_subject_info sub ON dpi.sid = sub.id left join dr_claims_loan loan ON loan.id = sub.lid left join dr_claims_customer cust ON cust.lid = loan.id left join dr_product_loan_record dplr on dplr.pid=dpi.id left join dr_member_base_info dmbi on dmbi.uid=dm.uid LEFT JOIN dr_product_invest dpit ON dpit.id = dpitr.investId
AND dpi.project_no is not null AND (dpit.fileStatus in(1,3) OR (dpit.fileStatus is NULL)) AND dpit.mchnt_txn_ssn is not NULL AND dpit.mchnt_txn_ssn != '' AND dpi.fileStatus = 2 AND LEFT(dpit.mchnt_txn_ssn,8) > '20170619' AND LEFT(dpit.mchnt_txn_ssn,8) < CURDATE()
AND dpit.failureCause = #{failureCauseType}
AND dpi.project_no is not null AND dpi.project_no != '' AND (dpit.fullFileStatus in (1,3) OR (dpit.fullFileStatus IS NULL)) AND dpit.remitMchntTxnSsn is not NULL and dpit.remitMchntTxnSsn != '' AND dpi.fileStatus = 2 AND LEFT(dpit.remitMchntTxnSsn,8) > '20170619' AND LEFT(dpit.remitMchntTxnSsn,8) < CURDATE()
AND dpit.fullFailureCause = #{failureCauseType}
AND dpi.project_no is not null and (dpitr.fileStatus in (1,3) OR (dpitr.fileStatus IS NULL)) AND (dpitr.remitMchntTxnSsn IS NOT NULL) AND dpitr.remitMchntTxnSsn !='' AND LEFT(dpitr.remitMchntTxnSsn,8) > '20170619' AND LEFT(dpitr.remitMchntTxnSsn,8) < CURDATE()
AND dpitr.failureCause = #{failureCauseType}
order by dpi.id asc limit #{offset},#{limit}
