mapper.select:
select concat(left(mbi.realname,1),'**') as realname,concat(left(m.mobilePhone,3),'****',right(m.mobilePhone,4)) as mobilePhone,ncr.addTime ,ncr.shouldtime,ncr.period,ifnull(p.`code`,'-') as pCode,ncr.amount,ncr.`status`,ncr.reward,m.recommCodes from js_novice_continue_record ncr left join dr_member m on ncr.uid = m.uid LEFT JOIN dr_member_base_info mbi on ncr.uid = mbi.uid LEFT JOIN dr_product_invest pi on ncr.investId = pi.id LEFT JOIN dr_product_info p on pi.pid = p.id
and INSTR(mbi.realname,#{realName}) > 0 and m.recommCodes = trim(#{recommCodes}) and INSTR(m.mobilePhone,#{mobilePhone}) > 0 and ncr.period = #{period} and ncr.status = #{status} and DATE_FORMAT(ncr.addTime,'%Y-%m-%d')>=DATE_FORMAT(#{startAddTime},'%Y-%m-%d') and DATE_FORMAT(#{endAddTime},'%Y-%m-%d')>=DATE_FORMAT(ncr.addTime,'%Y-%m-%d') and DATE_FORMAT(ncr.shouldtime,'%Y-%m-%d')>=DATE_FORMAT(#{startShouldTime},'%Y-%m-%d') and DATE_FORMAT(#{endShouldTime},'%Y-%m-%d')>=DATE_FORMAT(ncr.shouldtime,'%Y-%m-%d')
order by ncr.addTime desc limit #{offset},#{limit}

select ncr.id,ncr.uid,ncr.amount,ncr.joinType
from js_novice_continue_record ncr

	 and ncr.period = #{period}  
	 and ncr.status = #{status}  
	and DATE_FORMAT(ncr.shouldtime,'%Y-%m-%d') = DATE_FORMAT(#{shouldTime},'%Y-%m-%d')

order by ncr.addTime asc

select DISTINCT period from js_novice_continue_record
where DATE_FORMAT(shouldtime,'%Y%m%d')=DATE_FORMAT(NOW(),'%Y%m%d')
	and `status`=0;
