mapper.select:
select   from js_message_push 
 
	 and status = #{status}
	 and platform = #{platform}
	 and type = #{type}
	 and title like concat('%',trim(#{title}),'%')
	
		
			and  (DATE_FORMAT(sendStartDate,'%Y-%m-%d') >= #{startDate} 
			or DATE_FORMAT(sendEndDate,'%Y-%m-%d') >= #{startDate} )
		
		
			and ( #{endDate} >= DATE_FORMAT(sendStartDate,'%Y-%m-%d')
			or #{endDate} >= DATE_FORMAT(sendEndDate,'%Y-%m-%d') )
		
	
	
		and  CURDATE() >= DATE_FORMAT(sendStartDate,'%Y-%m-%d') 
			and DATE_FORMAT(sendEndDate,'%Y-%m-%d') >= CURDATE() 
	


	order by ${orders}


	limit #{offset},#{limit}

select count(1)  from js_message_push 
 
	 and status = #{status}
	 and platform = #{platform}
	 and type = #{type}
	 and title like concat('%',trim(#{title}),'%')
	
		
			and  (DATE_FORMAT(sendStartDate,'%Y-%m-%d') >= #{startDate} 
			or DATE_FORMAT(sendEndDate,'%Y-%m-%d') >= #{startDate} )
		
		
			and ( #{endDate} >= DATE_FORMAT(sendStartDate,'%Y-%m-%d')
			or #{endDate} >= DATE_FORMAT(sendEndDate,'%Y-%m-%d') )
		
	
	
	and  CURDATE() >= DATE_FORMAT(sendStartDate,'%Y-%m-%d') 
			and DATE_FORMAT(sendEndDate,'%Y-%m-%d') >= CURDATE()

mapper.update:
UPDATE js_message_push
title = #{title}, content = #{content}, status = #{status}, type = #{type}, city = #{city}, isInvest = #{isInvest}, weal = #{weal}, investMax = #{investMax}, investMin = #{investMin}, balanceMax = #{balanceMax}, balanceMin = #{balanceMin}, payment = #{payment}, liveness = #{liveness}, livenessType = #{livenessType}, sendStartDate = #{sendStartDate}, sendEndDate = #{sendEndDate}, sendTime = #{sendTime}, upDateTime = #{upDateTime}, updateUser = #{updateUser}, scheduleId = #{scheduleId}, sendno = #{sendno}, ischeck = #{ischeck}, audienceId = #{audienceId}, channel = #{channel}, url = #{url}, platform = #{platform}, platform = null,
where id = #{id}

UPDATE js_message_push
title = #{title}, content = #{content}, status = #{status}, type = #{type}, city = #{city}, city = null, isInvest = #{isInvest}, isInvest = null, weal = #{weal}, weal = null, investMax = #{investMax}, investMin = #{investMin}, investMax = null, investMin = null, balanceMax = #{balanceMax}, balanceMin = #{balanceMin}, balanceMax = null, balanceMin = null, payment = #{payment}, liveness = #{liveness}, payment = null, liveness = null, livenessType = #{livenessType}, sendStartDate = #{sendStartDate}, sendEndDate = #{sendEndDate}, sendTime = #{sendTime}, upDateTime = #{upDateTime}, updateUser = #{updateUser}, ischeck = #{ischeck}, audienceId = #{audienceId}, channel = #{channel}, scheduleId = #{scheduleId}, url = #{url}, platform = #{platform}, platform = null,
where id = #{id}

mapper.select:
SELECT dm.uid from dr_member dm where dm.uid in ( SELECT DISTINCT dev.uid from js_push_device dev LEFT JOIN js_push_app app ON dev.appId = app.id where app.id and dev.id
and app.type = #{channel}
and app.device = #{platform}
)
and dm.uid
not
in (SELECT dpi.uid from dr_product_invest dpi LEFT JOIN dr_product_info dp ON dpi.pid = dp.id where dp.type =2 GROUP BY dpi.uid )
and dm.uid
not
in (SELECT dmf.uid from dr_member_favourable dmf where dmf.`status` =0 GROUP BY dmf.uid )
and dm.uid in ( SELECT uid from dr_member_funds where 1=1
and balance+fuiou_balance >= #{balanceMin}
and #{balanceMax} >= balance+fuiou_balance
)
and dm.uid in ( SELECT DISTINCT uid from dr_product_invest_repayinfo WHERE #{payment} >= DATEDIFF(shouldTime,CURDATE()) and DATEDIFF(shouldTime,CURDATE())>=0 )
and dm.uid in ( SELECT dm.uid from dr_member dm where dm.uid not
in (SELECT dl.uid from dr_member_app_login_log dl WHERE #{liveness} >= DATEDIFF(CURDATE(),dl.addtime) ) )
and dm.uid in ( SELECT DISTINCT dpi.uid from dr_product_invest dpi LEFT JOIN dr_product_info dp ON dpi.pid = dp.id where dp.type = 2 GROUP BY dpi.uid,DATE_FORMAT(dpi.investTime,'%Y-%m-%d') HAVING
and SUM(dpi.amount)>=#{investMin}
and #{investMax}>=SUM(dpi.amount)
)
