mapper.select:
select l.*,u.name as addName
 from  dr_giftCard_setUp_detail l 
LEFT JOIN sys_users u ON l.addUser = u.user_ky

	 and parentId = #{parentId}

order by l.status desc
LIMIT #{offset},#{limit}

select l.id,l.parentId,concat(substring(l.giftCard,1,LENGTH(l.giftCard)-4),"****") as giftCard, l.status,l.investId,l.issueTime,l.uid,l.addTime,h.`name` as name,c.`name` as channelName, concat(LEFT(m.mobilePhone,3),"****",RIGHT(m.mobilePhone,4)) mobilePhone, pi.amount as investAmount,p.deadline as deadline,u.name as addName from dr_giftCard_setUp_detail l left JOIN dr_giftCard_setUp h ON l.parentId = h.id LEFT JOIN dr_channel_info c ON h.channelId = c.id LEFT JOIN dr_member m ON l.uid = m.uid LEFT JOIN dr_product_invest pi ON l.investId = pi.id LEFT JOIN dr_product_info p ON pi.pid = p.id LEFT JOIN sys_users u ON l.addUser = u.user_ky
and l.parentId = #{parentId}
and h.channelId = #{channelId}
and instr(h.name, #{name} )>0
and DATE_FORMAT(l.issueTime,'%Y%m%d') >= DATE_FORMAT(#{startDate},'%Y%m%d')
and DATE_FORMAT(l.issueTime,'%Y%m%d') <= DATE_FORMAT(#{endDate},'%Y%m%d')
order by l.status desc LIMIT #{offset},#{limit}

select count(1) from dr_giftCard_setUp_detail l left JOIN dr_giftCard_setUp h ON l.parentId = h.id
and l.parentId = #{parentId}
and h.channelId = #{channelId}
and instr(h.name, #{name} )>0
and DATE_FORMAT(l.issueTime,'%Y%m%d') >= DATE_FORMAT(#{startDate},'%Y%m%d')
and DATE_FORMAT(l.issueTime,'%Y%m%d') <= DATE_FORMAT(#{endDate},'%Y%m%d')
