mapper.select:
select id as id,name as name,date_format(createTime,'%Y-%m-%d %H:%i:%s') as createTime, codeFixation as codeFixation,digit as digit from js_activity_template where 1=1
and name LIKE CONCAT('%', #{name,jdbcType=VARCHAR},'%')
and createTime > #{startTime:VARCHAR}
and #{endTime:VARCHAR} > createTime
order by createTime desc limit #{offset},#{limit}

mapper.insert:
insert into js_activity_prize (
atid,
name,
amount
)
values
(#{atid:INTEGER},
#{name},
#{amount}
)

mapper.update:
update js_activity_template set 
name=#{name:VARCHAR},
codeFixation=#{codeFixation:VARCHAR},
digit=#{digit:INTEGER}
where id = #{id:INTEGER}

update js_activity_product set 
prizeCode=#{prizeCode:VARCHAR},
prizeImgUrl=#{prizeImgUrl:VARCHAR},
status=3,
prizeUrl=#{prizeUrl:VARCHAR}
where id = #{id:INTEGER}

mapper.select:
select id as id ,atid as atid ,pid as pid ,prizeCode as prizeCode ,status as status,prizeImgUrl as prizeImgUrl,activityPeriods as activityperriods 
from js_activity_product where 1=1

	and id = #{id:INTEGER}

select jap.id as id,CONCAT(jat.name,'-',dpi.fullName,jap.activityPeriods) as name,date_format(dpi.startDate,'%Y-%m-%d %H:%i:%s') as startDate, jap.status as status,sc.cnvalue as cnvalue, jap.prizeCode as prizeCode,jap.prizeUrl as prizeUrl from js_activity_product jap inner join js_activity_template jat on jat.id=jap.atid inner join dr_product_info dpi on dpi.id =jap.pid inner join sys_chooseoption sc on sc.code=dpi.status and sc.category='productStatus' where 1=1
and jat.name LIKE CONCAT('%', #{name,jdbcType=VARCHAR},'%')
and dpi.fullName LIKE CONCAT('%', #{productname,jdbcType=VARCHAR},'%')
and jap.status = #{status,jdbcType=INTEGER}
and dpi.startDate > #{startTime:VARCHAR}
and #{endTime:VARCHAR} > dpi.startDate
order by dpi.startDate desc,jap.activityPeriods limit #{offset},#{limit}

select count(*) from js_activity_product jap inner join js_activity_template jat on jat.id=jap.atid inner join dr_product_info dpi on dpi.id =jap.pid inner join sys_chooseoption sc on sc.code=dpi.status and sc.category='productStatus' where 1=1
and jat.name LIKE CONCAT('%', #{name,jdbcType=VARCHAR},'%')
and dpi.fullName LIKE CONCAT('%', #{productname,jdbcType=VARCHAR},'%')
and jap.status = #{status,jdbcType=INTEGER}
and dpi.startDate > #{startTime:VARCHAR}
and #{endTime:VARCHAR} > dpi.startDate

select api.id as id,api.isUplod as isUplod,api.prizeStatus as prizeStatus, concat(substring(dmbi.realname,1,1),"**") AS realname, concat(substring(dm.mobilePhone,1,3),"***",substring(dm.mobilePhone,8)) AS mobilePhone,dm.recommCodes as recommCodes, pi.amount as amount,date_format(pi.investTime,'%Y-%m-%d %H:%i:%s') as investTime, api.luckCodes,api.prizeMobile as prizeMobile,api.prizeContent as prizeContent,api.prizeImgUrl as prizeImgUrl, api.prizeHeadPhoto as prizeHeadPhoto,api.prizeVideoLink as prizeVideoLink ,api.prizeVideoUrl as prizeVideoUrl from js_activity_product_invest_info api inner join dr_product_invest pi on api.piid=pi.id inner join dr_member dm on dm.uid=pi.uid inner join dr_member_base_info dmbi on dmbi.uid=dm.uid inner join js_activity_product jap on jap.pid=pi.pid where jap.id=#{id:INTEGER}
and api.luckCodes like CONCAT('%', #{luckCodes,jdbcType=VARCHAR},'%')
and api.prizeStatus = #{prizeStatus,jdbcType=INTEGER}
limit #{offset},#{limit}

select count(*) from js_activity_product_invest_info api inner join dr_product_invest pi on api.piid=pi.id inner join dr_member dm on dm.uid=pi.uid inner join dr_member_base_info dmbi on dmbi.uid=dm.uid inner join js_activity_product jap on jap.pid=pi.pid where jap.id=#{id:INTEGER}
and api.luckCodes like CONCAT('%', #{luckCodes,jdbcType=VARCHAR},'%')
and api.prizeStatus = #{prizeStatus,jdbcType=INTEGER}

