mapper.select:
		select 
			id,extendId,type,status,imgUrl,showSort
 		from 
 			js_activity_extend_pic where  extendId = #{extendId} and type  = #{type} 
			
				and status = #{status}

mapper.update:
UPDATE js_activity_extend_pic
status=#{status}, imgUrl=#{imgUrl}, showSort=#{showSort},
WHERE id=#{id}

UPDATE js_activity_offline
status = #{status}, activityTime = #{activityTime}, pcBanner = #{pcBanner}, h5Banner = #{h5Banner}, h5ListBanner = #{h5ListBanner}, videoUrl = #{videoUrl}, imgUrl = #{imgUrl}, titleList = #{titleList}, title = #{title}, summary = #{summary}, content = #{content},
WHERE id=#{id}
