mapper.select:
select CASE WHEN dmbi.sex=1 then concat(substring(dmbi.realname,1,1),'先生') when dmbi.sex=2 then concat(substring(dmbi.realname,1,1),'女士') ELSE '' END as realName, dm.mobilePhone as mobilePhone,ff.*, case when ff.status=0 then '未处理' else '已处理' end as statusName,dm.recommCodes as recommCodes from dr_feedback ff left JOIN dr_member dm ON ff.uid = dm.uid left join dr_member_base_info dmbi ON ff.uid = dmbi.uid where 1=1
and ff.status = #{status}
and dm.mobilePhone like CONCAT('%',${mobilePhone},'%' )
order by feedbackTime desc limit #{offset},#{limit}

select count(1) from dr_feedback ff left JOIN dr_member dm ON ff.uid = dm.uid where 1=1
and ff.status = #{status}
and dm.mobilePhone like CONCAT('%',${mobilePhone},'%' )
