mapper.select:
SELECT pr.id,pr.`name`,pr.periods,CONCAT(DATE_FORMAT(pr.startTime,'%Y-%m-%d %H:%i:%s'),'-', DATE_FORMAT(pr.endTime,'%Y-%m-%d %H:%i:%s')) dateInterval,pr.`status`,count(prl.id) total,sum(prl.amount) totalAmount, su.name as addName,pr.addTime FROM js_product_reservation pr LEFT JOIN js_product_reservation_log prl on pr.id = prl.prid left join sys_users su on pr.addUser =su.user_ky
and INSTR(pr.name,#{name:VARCHAR})>0
and DATE_FORMAT(pr.startTime,'%Y-%m-%d %H:%i:%s') >= DATE_FORMAT( #{startTime:TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and DATE_FORMAT(pr.endTime,'%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endTime:TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and pr.status = #{status:INTEGER}
GROUP BY pr.id order by pr.periods desc limit #{offset},#{limit}

select count(1)
from  js_product_reservation a

	
		and INSTR(a.name,#{name:VARCHAR})>0 
	
	
		and DATE_FORMAT(a.startTime,'%Y-%m-%d %H:%i:%s')  >=  DATE_FORMAT(#{startTime:TIMESTAMP},'%Y-%m-%d %H:%i:%s')
	
	
		and DATE_FORMAT(a.endTime,'%Y-%m-%d %H:%i:%s')  <=  DATE_FORMAT(#{endTime:TIMESTAMP},'%Y-%m-%d %H:%i:%s')
	
	
		and a.status = #{status:INTEGER}
	
	
		and a.periods = #{periods:INTEGER}
	
	
		and a.status <> #{notStatus:INTEGER}

mapper.update:
update js_product_reservation
name = #{name}, startTime = #{startTime}, endTime = #{endTime}, status = #{status}, updateUser = #{updateUser}, updateTime = SYSDATE()
id = #{id:INTEGER}

mapper.select:
select  from js_product_reservation

	
		and periods = #{periods}
	


	order by ${order}


	limit #{offset},#{limit}

update js_product_reservation 
	set updateUser = 0
		,updateTime = now()
		 , status = #{status}
where id = #{id}
